mapper.select:
	   select * from 
       m_receiptads

        1=1

			 and user_id=#{user_id}


			 and addressId=#{addressId}


			 and status=#{status}

    select a.goods_id,a.name,a.price,b.count,a.mktprice,a.big_pic,date_format(b.addTime,'%Y-%c-%d %H:%i:%s'),c.shop_id,c.shop_name from sdb_b2c_goods a,m_cart b,m_shop c

	 	a.goods_id=b.goods_id and b.user_id=#{0}
        and store > 0 and a.shop_id=c.shop_id

mapper.delete:
		delete from m_cart
		where user_id=#{user_id} and goods_id in 

            #{goodsIds}

mapper.update:
update m_cart
set count=#{0}
where user_id=#{1} and goods_id=#{2}

		update m_cart
		set reckoning=#{reckoning}
		where user_id=#{user_id} 

			 and goods_id in

            #{goodsId}

mapper.select:
    select a.goods_id,a.name,a.price,a.mktprice,a.big_pic,date_format(b.addTime,'%Y-%c-%d %H:%i:%s'),c.shop_id,c.shop_name from sdb_b2c_goods a,m_cart b,m_shop c

	 	a.goods_id=b.goods_id and b.user_id=#{0}
        and store > 0 and a.shop_id=c.shop_id and reckoning=1

mapper.update:
		update m_receiptads
		set status=#{status}

        1=1

			 and user_id=#{user_id}


